mapper.select:
select a.id id,
	   a.shopping_cart_id shoppingCartId,
	   a.product_id productId,
	   a.count count
  from tb_shopping_cart_details a,
       tb_shopping_cart b
 where a.shopping_cart_id = b.id
   and b.user_id = #{userId}
   and a.product_id = #{productId}

mapper.delete:
Delete from tb_shopping_cart_details 
where id in 

  #{id}
