mapper.select:
select *
  from tb_user_coupon
 where user_id = #{userId}
   and status = #{status}
   and str_to_date(exp_date,'%Y-%m-%d') <= str_to_date(sysdate(),'%Y-%m-%d')

select count(id)
  from tb_user_coupon
 where user_id = #{userId}
   and status = #{status}
   and str_to_date(exp_date,'%Y-%m-%d') <= str_to_date(sysdate(),'%Y-%m-%d')
